function startAutoSlide(){intervalId=setInterval(()=>{index=(index+1)%imgelements.length,updatecarousel(),updateselectimg(index)},3e3)}function stopAutoSlide(){clearInterval(intervalId)}function previewimg(){stopAutoSlide();index=Math.max(index-1,0);updatecarousel();updateselectimg(index);startAutoSlide()}function nextimg(){stopAutoSlide();index=Math.min(index+1,imgelements.length-1);updatecarousel();updateselectimg(index);startAutoSlide()}function updatecarousel(){container.style.transform=`translateX(-${index*imgwidth}px)`}function updateselectimg(n){selectimgelements.forEach((t,i)=>{i!=n?t.classList.remove("btn_selected_img"):t.classList.add("btn_selected_img")})}var index=0,imgelements=document.querySelectorAll(".carousel_img"),carousel_btn_preview=document.getElementById("carousel_btn_preview"),carousel_btn_next=document.getElementById("carousel_btn_next"),selectimgelements=document.querySelectorAll(".btn_select_img"),container=document.querySelector(".carousel_container"),uplimit=imgelements.length-1,imgwidth=imgelements[0].clientWidth,intervalId;window.addEventListener("load",()=>{for(let n=0;n<imgelements.length;n++)n==0&&selectimgelements[n].classList.add("btn_selected_img");startAutoSlide()});selectimgelements.forEach(function(n,t){n.addEventListener("click",()=>{for(let i=0;i<selectimgelements.length;i++)i!=t?selectimgelements[i].classList.remove("btn_selected_img"):(n.classList.add("btn_selected_img"),index=t);updatecarousel();startAutoSlide()})});